// Function to move the image automatically and check for collision
int moveImageAutomatically1(uint16_t startY, uint16_t endY, const uint16_t *image,int x,int y) {
	static uint16_t xPos = 66; // Current X position of car
    static int direction = 1; // Movement direction 
    uint16_t width = 12; // Width of car
	int hinverted = 0;
	int number = 0; // returned value
	static int speed = 1;

	
    // Increment or decrement speed of car
    xPos += direction * speed;

    // Check if xPos reaches the right or left boundary
    if (xPos >= (126 - width) || xPos <= 0) {
        // Change direction when reaching the boundary
        direction *= -1;
		hinverted = 1;
    }

	// generating random speeds for each level
	if(score==0)
	{
		speed = 1;
	} // end if

	else if (score==1)
	{
		generateRandomNumber(2,4);
		speed=randomNum;
		
	} // end else if

	else if (score==2)
	{ 
		generateRandomNumber(1,5);
		speed=randomNum;
	} // end else if

	else if (score==3)
	{
		generateRandomNumber(4,7);
		speed=randomNum;
	} // end else if

	else if (score==4)
	{
		generateRandomNumber(6,10);
		speed=randomNum;
	} // end else if

    // Draws the car at the current position
    putImage(xPos, startY,16,14, image, 0, 0); // Assuming no inversion

	// checks collision with car
	if (isInside(xPos,startY,16,14,x,y) || isInside(xPos,startY,16,14,x+12,y) || isInside(xPos,startY,16,14,x,y+16) || isInside(xPos,startY,16,14,x+16,y+16))
	{
		greenOff(); // turns green led off
		redOn(); // turns red on
		delay(1000); 
		number=2; // returning 2 for restart menu
		fillRectangle(0,0,128,160,0); // screen black
		eputs("GAME OVER! You crashed!\n"); 
	}

	return number;

}

